package com.datatype;

public class DatatypeDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte myNum  = 19;                                     //byte integer
		short myValue = 333;                                   //short integer 
		int noOfRooms = 200;                                //Integer (whole number)
		long distanceToSun = 98000000L;            // long integer

		
		double   weightOfPotato = 5.984;             // double data type can take fractions
		float       myFloatNum = 5.99f;                   // Floating point number

		char  myLetter = 97;                               // Character   must be in single quotes
		boolean myBool = false;                              // Boolean 

		
		System.out.println(myLetter);
		
		
		
	}

}
