package com.inheritance;

class Animal2{
	void eat() {
		System.out.println("animal eating....");
	}
}

class Dog2 extends Animal2{
	void bark() {
		System.out.println("wof wof ....");
	}
}

class Cat extends Animal2{
	void meow() {
		System.out.println("Meowing.....");
	}
}


